document.getElementById("search-input").addEventListener("input", function(event) {
  event.preventDefault()
  console.log("searching started")
  const data = document.getElementById("search-input").value.toLowerCase();

  if (data === "") {
      document.getElementById("menu-items").innerHTML = result;
  } else {
      const filteredItems = menu.filter(item => {
          const originalData = item.title.toLowerCase();
          return originalData.includes(data);
      });

      const filteredHTML = filteredItems.map(item => {
          return `<div class="menu-item ">
              <div class="card shadow-lg p-3 mb-5 bg-white rounded">
                  <img src="${item.img}" class="card-img-top img-fluid" alt="${item.title}">
                  <div class="card-body">
                      <h5 class="card-title text-success">${item.title}</h5>
                      <h6 class="card-subtitle mb-2 text-muted">${item.price}</h6>
                      <p class="card-text text-danger">${item.description}</p>
                      <p class="card-text"><div class="text-muted">Category: ${item.category}</div></p>
                  </div>
              </div>
          </div>`;
      });

      document.getElementById("menu-items").innerHTML = filteredHTML.join(" ");
  }
});
